   143→// CheckAuthStatus polls authorization status, returns: "pending", "completed", "failed"
   144→func (a *App) CheckAuthStatus(authID string) string {
   145→	state := a.deviceFlow.GetStatus(authID)
   146→	if state == nil {
   147→		return "failed"
   148→	}
   149→	if state.Status == "completed" && state.Result != nil {
   150→		acc := config.Account{
   151→			ClientID:     state.ClientID,
   152→			ClientSecret: state.ClientSecret,
   153→			RefreshToken: state.Result.RefreshToken,
   154→			AccessToken:  state.Result.AccessToken,
   155→		}
   156→		// 随机延迟 2-5 秒，避免注册后立即调用 API 被检测
   157→		time.Sleep(time.Duration(2+rand.Intn(4)) * time.Second)
   158→		// 获取配额信息
   159→		aqClient := client.NewAmazonQClient()
   160→		machineId := acc.GetMachineId()
   161→		quota, err := aqClient.GetQuota(context.Background(), acc.AccessToken, machineId)
   162→		if err != nil {
   163→			logger.Error().Err(err).Str("machineId", machineId).Msg("添加账号时获取配额失败")
   164→		} else {
   165→			logger.Info().Str("email", quota.Email).Float64("baseLimit", quota.BaseLimit).Msg("添加账号获取配额成功")
   166→			// 检查 email 是否已存在
   167→			if config.EmailExists(quota.Email) {
   168→				a.deviceFlow.RemoveFlow(authID)
   169→				return "duplicate"
   170→			}
   171→			acc.Email = quota.Email
   172→			acc.LastQuotaCheck = time.Now().Unix()
   173→			acc.BaseUsed = quota.BaseUsed
   174→			acc.BaseLimit = quota.BaseLimit
   175→			acc.BaseResetTime = quota.BaseResetTime
   176→			acc.TrialStatus = quota.TrialStatus
   177→			acc.TrialUsed = quota.TrialUsed
   178→			acc.TrialLimit = quota.TrialLimit
   179→			acc.TrialExpiry = quota.TrialExpiry
   180→		}
   181→		config.AddAccount(acc)
   182→		config.Save()
   183→		a.deviceFlow.RemoveFlow(authID)
   184→	}
   185→	return state.Status
   186→}
