   206→	cfg := config.Get()
   207→	if cfg == nil {
   208→		return QuotaStats{}
   209→	}
   210→	var used, total float64
   211→	var calls int64
   212→	for _, acc := range cfg.Accounts {
   213→		total += (acc.BaseLimit + acc.TrialLimit) * 5000
   214→		used += (acc.BaseUsed + acc.TrialUsed) * 5000
   215→		calls += acc.CallCount
   216→	}
   217→	return QuotaStats{Used: int64(used), Total: int64(total), Calls: calls}
