   243→// RefreshAllQuotas 刷新配额，每次处理16个（批内并发）
   244→func (a *App) RefreshAllQuotas(offset int) RefreshQuotaResult {
   245→	deduplicated := 0
   246→	if offset == 0 {
   247→		config.Load()
   248→		// 刷新前去重
   249→		deduplicated = config.DeduplicateByEmail()
   250→		if deduplicated > 0 {
   251→			config.Save()
   252→			logger.Info().Int("deduplicated", deduplicated).Msg("刷新前去重完成")
   253→		}
   254→	}
   255→	cfg := config.Get()
   256→	if cfg == nil || len(cfg.Accounts) == 0 {
   257→		return RefreshQuotaResult{Done: true, Deduplicated: deduplicated}
   258→	}
   259→
   260→	total := len(cfg.Accounts)
   261→	if offset >= total {
   262→		return RefreshQuotaResult{Total: total, Processed: total, Deduplicated: deduplicated, Done: true}
   263→	}
   264→	batchSize := 16
   265→	removed := 0
   266→	aqClient := client.NewAmazonQClient()
   267→
   268→	end := offset + batchSize
   269→	if end > total {
   270→		end = total
   271→	}
   272→	logger.Info().Int("offset", offset).Int("end", end).Int("total", total).Msg("刷新配额批次开始")
   273→
   274→	var wg sync.WaitGroup
   275→	results := make(chan quotaResult, end-offset)
   276→
   277→	// 批内并发请求
   278→	for i := offset; i < end; i++ {
   279→		wg.Add(1)
   280→		go func(idx int) {
   281→			defer wg.Done()
   282→			acc := &cfg.Accounts[idx]
   283→			r := quotaResult{index: idx}
   284→			machineId := acc.GetMachineId()
   285→
   286→			quota, err := aqClient.GetQuota(context.Background(), acc.AccessToken, machineId)
   287→			// 403时刷新token重试
   288→			if err != nil && strings.Contains(err.Error(), "403") {
   289→				logger.Info().Int("index", idx).Str("email", acc.Email).Str("error", err.Error()).Msg("刷新配额403，尝试刷新token")
   290→				if api.RefreshToken(acc) == nil {
   291→					quota, err = aqClient.GetQuota(context.Background(), acc.AccessToken, machineId)
   292→				}
   293→			}
   294→
   295→			// 检测封禁
   296→			if err != nil && strings.Contains(err.Error(), "TEMPORARILY_SUSPENDED") {
   297→				r.isBanned = true
   298→				logger.Warn().Int("index", idx).Str("email", acc.Email).Str("error", err.Error()).Msg("检测到封禁")
   299→			} else {
   300→				r.quota = quota
   301→				r.err = err
   302→			}
   303→			results <- r
   304→		}(i)
   305→	}
   306→
   307→	go func() { wg.Wait(); close(results) }()
   308→
   309→	// 收集结果，统一更新
   310→	var toRemove []string
   311→	for r := range results {
   312→		acc := &cfg.Accounts[r.index]
   313→		if r.newAccess != "" {
   314→			acc.AccessToken = r.newAccess
   315→			if r.newRefresh != "" {
   316→				acc.RefreshToken = r.newRefresh
   317→			}
   318→		}
   319→		if r.isBanned {
   320→			logger.Warn().Str("email", acc.Email).Msg("刷新配额时检测到封禁，删除账号")
   321→			toRemove = append(toRemove, acc.ClientID)
   322→			continue
   323→		}
   324→		if r.quota != nil {
   325→			acc.Email = r.quota.Email
   326→			acc.LastQuotaCheck = time.Now().Unix()
   327→			acc.BaseUsed = r.quota.BaseUsed
   328→			acc.BaseLimit = r.quota.BaseLimit
   329→			acc.BaseResetTime = r.quota.BaseResetTime
   330→			acc.TrialStatus = r.quota.TrialStatus
   331→			acc.TrialUsed = r.quota.TrialUsed
   332→			acc.TrialLimit = r.quota.TrialLimit
   333→			acc.TrialExpiry = r.quota.TrialExpiry
   334→		} else if r.err != nil {
   335→			logger.Error().Err(r.err).Str("email", acc.Email).Msg("刷新配额失败")
   336→		}
   337→	}
   338→
   339→	for _, clientID := range toRemove {
   340→		config.RemoveAccount(clientID)
   341→		removed++
   342→	}
   343→	// 最后一批时进行刷新后去重
   344→	postDedup := 0
   345→	if end >= total {
   346→		postDedup = config.DeduplicateByEmail()
   347→		if postDedup > 0 {
   348→			logger.Info().Int("deduplicated", postDedup).Msg("刷新后去重完成")
   349→		}
   350→	}
   351→	config.Save()
   352→	logger.Info().Int("processed", end).Int("removed", removed).Msg("刷新配额批次完成")
   353→
   354→	return RefreshQuotaResult{
   355→		Total:        total,
   356→		Processed:    end,
   357→		Removed:      removed,
   358→		Deduplicated: deduplicated + postDedup,
   359→		Done:         end >= total,
   360→	}
   361→}
