   363→// ConfigureClaudeCode 一键配置 Claude Code（兼容 cc-switch，跨平台）
   364→func (a *App) ConfigureClaudeCode() error {
   365→	homeDir, err := os.UserHomeDir()
   366→	if err != nil {
   367→		return fmt.Errorf("无法获取用户目录: %w", err)
   368→	}
   369→	configPath := filepath.Join(homeDir, ".claude", "settings.json")
   370→
   371→	// 读取现有配置
   372→	var settings map[string]any
   373→	if data, err := os.ReadFile(configPath); err == nil {
   374→		json.Unmarshal(data, &settings)
   375→	}
   376→	if settings == nil {
   377→		settings = make(map[string]any)
   378→	}
   379→
   380→	// 获取当前端口
   381→	port := a.GetPort()
   382→	baseURL := fmt.Sprintf("http://127.0.0.1:%d", port)
   383→
   384→	// 合并 env 配置（保留现有 env 中的其他字段，兼容 cc-switch 格式）
   385→	var env map[string]any
   386→	if existingEnv, ok := settings["env"].(map[string]any); ok {
   387→		env = existingEnv
   388→	} else {
   389→		env = make(map[string]any)
   390→	}
   391→	// 基础配置（不设置模型变量，兼容 cc-switch）
   392→	env["ANTHROPIC_BASE_URL"] = baseURL
   393→	env["ANTHROPIC_AUTH_TOKEN"] = "123456"
   394→	settings["env"] = env
   395→
   396→	// 确保目录存在
   397→	if err := os.MkdirAll(filepath.Dir(configPath), 0755); err != nil {
   398→		return fmt.Errorf("无法创建目录: %w", err)
   399→	}
   400→
   401→	// 原子写入（与 cc-switch 一致）
   402→	data, _ := json.MarshalIndent(settings, "", "  ")
   403→	tmpPath := fmt.Sprintf("%s.tmp.%d", configPath, time.Now().UnixNano())
   404→
   405→	f, err := os.OpenFile(tmpPath, os.O_WRONLY|os.O_CREATE|os.O_TRUNC, 0600)
   406→	if err != nil {
   407→		return fmt.Errorf("无法创建临时文件: %w", err)
   408→	}
   409→	if _, err := f.Write(data); err != nil {
   410→		f.Close()
   411→		os.Remove(tmpPath)
   412→		return fmt.Errorf("无法写入临时文件: %w", err)
   413→	}
   414→	if err := f.Sync(); err != nil { // 确保数据写入磁盘
   415→		f.Close()
   416→		os.Remove(tmpPath)
   417→		return fmt.Errorf("无法同步文件: %w", err)
   418→	}
   419→	f.Close()
   420→
   421→	// Windows 特殊处理：rename 前先删除目标文件
   422→	if runtime.GOOS == "windows" {
   423→		os.Remove(configPath)
   424→	}
   425→	if err := os.Rename(tmpPath, configPath); err != nil {
   426→		os.Remove(tmpPath)
   427→		return fmt.Errorf("无法保存配置: %w", err)
   428→	}
   429→
   430→	// 强制设置系统环境变量
   431→	a.setSystemEnvVars(homeDir, port)
   432→
   433→	// 配置 ~/.factory/config.json
   434→	a.configureFactoryConfig(homeDir, port)
   435→
   436→	return nil
   437→}
