   476→// dedupeModelsByModel 根据model字段去重，删除旧项保留新项（用于config.json）
   477→func (a *App) dedupeModelsByModel(existing any, defaults []map[string]any, baseURL string) []map[string]any {
   478→	defaultSet := make(map[string]map[string]any)
   479→	for _, m := range defaults {
   480→		if model, ok := m["model"].(string); ok {
   481→			defaultSet[model] = m
   482→		}
   483→	}
   484→	var result []map[string]any
   485→	if arr, ok := existing.([]any); ok {
   486→		for _, item := range arr {
   487→			if m, ok := item.(map[string]any); ok {
   488→				if model, ok := m["model"].(string); ok {
   489→					if _, isDefault := defaultSet[model]; isDefault {
   490→						continue // 删除旧的默认模型项
   491→					}
   492→				}
   493→				// 更新所有模型的 base_url 和 api_key
   494→				m["base_url"] = baseURL
   495→				m["api_key"] = "123456"
   496→				result = append(result, m)
   497→			}
   498→		}
   499→	}
   500→	// 对result按model去重，保留最后一个
   501→	seen := make(map[string]int)
   502→	for i, m := range result {
   503→		if model, ok := m["model"].(string); ok {
   504→			seen[model] = i
   505→		}
   506→	}
   507→	var deduped []map[string]any
   508→	for i, m := range result {
   509→		if model, ok := m["model"].(string); ok {
   510→			if seen[model] == i {
   511→				deduped = append(deduped, m)
   512→			}
   513→		} else {
   514→			deduped = append(deduped, m)
   515→		}
   516→	}
   517→	// 添加新的默认模型
   518→	return append(deduped, defaults...)
   519→}
