   545→			}
   546→			f, err := os.OpenFile(rcPath, os.O_APPEND|os.O_CREATE|os.O_WRONLY, 0644)
   547→			if err == nil {
   548→				f.WriteString("\n" + line1 + "\n" + line2 + "\n" + line3 + "\n")
