   581→		}
   582→	}()
   583→
   584→	select {
   585→	case r := <-ch:
   586→		logger.Info().Str("source", r.source).Msg("刷新云配置成功")
   587→		config.SetCloudConfig(r.cfg)
   588→		return config.SaveCloudConfig()
   589→	case <-time.After(3 * time.Second):
   590→	}
   591→
   592→	// fallback
   593→	if cfg, err := client.FetchCloudConfig(); err == nil && cfg != nil && len(*cfg) > 0 {
   594→		logger.Info().Str("source", "explame.com").Msg("刷新云配置成功")
   595→		config.SetCloudConfig(cfg)
   596→		return config.SaveCloudConfig()
   597→	}
   598→
   599→	logger.Error().Msg("刷新云配置失败")
