   702→// ClaimTokens 自动取号（仅永久版用户可用）
   703→func (a *App) ClaimTokens() (ClaimTokensResult, error) {
   704→	vInfo := activation.GetVersionInfo()
   705→	if vInfo.ExpireTime != "永久" {
   706→		return ClaimTokensResult{}, fmt.Errorf("自动加号功能仅限永久版用户使用")
   707→	}
   708→
   709→	code := activation.GetActivationCode()
   710→	if code == "" {
   711→		return ClaimTokensResult{}, fmt.Errorf("未找到激活码")
   712→	}
   713→
   714→	payload := map[string]string{"user_id": code}
   715→	data, _ := json.Marshal(payload)
   716→	resp, err := http.Post("https://explame.com/api/v1/claim", "application/json", bytes.NewReader(data))
   717→	if err != nil {
   718→		return ClaimTokensResult{}, fmt.Errorf("取号请求失败: %w", err)
   719→	}
   720→	defer resp.Body.Close()
   721→
   722→	body, _ := io.ReadAll(resp.Body)
   723→	var result struct {
   724→		Tokens []struct {
   725→			ClientID     string `json:"client_id"`
   726→			ClientSecret string `json:"client_secret"`
   727→			AccessToken  string `json:"access_token"`
   728→			RefreshToken string `json:"refresh_token"`
   729→			MachineId    string `json:"machine_id"`
   730→		} `json:"tokens"`
   731→		Error string `json:"error"`
   732→	}
   733→	if err := json.Unmarshal(body, &result); err != nil {
   734→		return ClaimTokensResult{}, fmt.Errorf("解析响应失败: %w", err)
   735→	}
   736→	if result.Error != "" {
   737→		return ClaimTokensResult{}, fmt.Errorf("%s", result.Error)
   738→	}
   739→
   740→	var added, duplicated int
   741→	for _, t := range result.Tokens {
   742→		if config.ClientIDExists(t.ClientID) {
   743→			duplicated++
   744→			continue
   745→		}
   746→		acc := config.Account{
   747→			ClientID:     t.ClientID,
   748→			ClientSecret: t.ClientSecret,
   749→			AccessToken:  t.AccessToken,
   750→			RefreshToken: t.RefreshToken,
   751→			MachineId:    t.MachineId,
   752→		}
   753→		config.AddAccount(acc)
   754→		added++
   755→	}
   756→	config.Save()
   757→	return ClaimTokensResult{Added: added, Duplicated: duplicated}, nil
   758→}
